# Find all data for a specific device, most recent results first
    query {
      transactions(
        tags: [
          { name: "App-Name", values: ["AirQuality-DePIN-v1.0"] }
          { name: "Device-ID", values: ["airmon-007"] }
        ]
        first: 100
        sort: HEIGHT_DESC
      ) {
        edges {
          node {
            id
            tags {
              name
              value
            }
            data {
              size
            }
          }
        }
      }
    }

# Find data by location
