  folderPath: "./my-website",
  dataItemOpts: {
    tags: [
      { name: "Bundle-Format", value: "binary" },
      { name: "Bundle-Version", value: "2.0.0" },
      { name: "App-Name", value: "MyWebsite-v2.1" },
      { name: "Version", value: "2.1.0" },
    ],
  },
});
