function YourComponent() {
  const request = useWayfinderRequest();
  const [data, setData] = useState(null);

  useEffect(() => {
    (async () => {
      const response = await request(`ar://${txId}`, {
        verificationSettings: {
          enabled: true,
          strict: true,
        },
      });
      const data = await response.arrayBuffer();
      setData(data);
    })();
  }, [request, txId]);

  return {data && {data}};
}
